// Sanitize and structure the bug report
const message = $input.first();
const content = message.json.content || '';

// Remove mentions, sanitize URLs, limit length
const sanitized = content
  .replace(/<@[!&]?\d+>/g, '[USER]')
  .replace(/https?:\/\/[^\s]+/g, '[URL]')
  .slice(0, 2000);

// Extract basic structure
const bugReport = {
  messageId: message.json.id,
  authorId: message.json.author?.id || 'unknown',
  authorName: message.json.author?.username || 'unknown',
  channelId: message.json.channel_id,
  timestamp: message.json.timestamp || new Date().toISOString(),
  rawContent: sanitized,
  searchableText: sanitized.toLowerCase().trim(),
  threadId: message.json.id
};

return { json: bugReport };

// Merge AI analysis with original bug data
const originalBug = $('Sanitize and Extract Bug Details').first().json;
const aiAnalysis = $input.first().json;

// Combine all data
const enrichedBug = {
  ...originalBug,
  analysis: aiAnalysis,
  searchableText: aiAnalysis.searchSummary || originalBug.searchableText
};

return { json: enrichedBug };

// Calculate similarity and determine if duplicate
const currentBug = $('Merge AI Analysis with Bug Data').first().json;
const searchResults = $input.all();

const SIMILARITY_THRESHOLD = 0.82;

if (searchResults.length === 0) {
  return {
    json: {
      isDuplicate: false,
      currentBug: currentBug,
      matchedBugs: []
    }
  };
}

const matches = searchResults
  .filter(result => {
    const score = result.json.metadata?.score || 0;
    return score >= SIMILARITY_THRESHOLD;
  })
  .sort((a, b) => (b.json.metadata?.score || 0) - (a.json.metadata?.score || 0));

if (matches.length > 0) {
  const bestMatch = matches[0];
  return {
    json: {
      isDuplicate: true,
      currentBug: currentBug,
      matchedBug: {
        threadId: bestMatch.json.metadata?.threadId,
        similarity: bestMatch.json.metadata?.score,
        content: bestMatch.json.pageContent
      },
      allMatches: matches.map(m => ({
        threadId: m.json.metadata?.threadId,
        similarity: m.json.metadata?.score
      }))
    }
  };
} else {
  return {
    json: {
      isDuplicate: false,
      currentBug: currentBug,
      matchedBugs: searchResults.map(r => ({
        threadId: r.json.metadata?.threadId,
        similarity: r.json.metadata?.score
      }))
    }
  };
}

